import { Component } from 'react';

class GetJsonUpdate extends Component {
    state = {
        jsonResult: null
    }

    fetchJsonUpdate = () => {
        fetch(`http://127.0.0.1:8080/api/json`)
            .then((response) => response.json())
            .then((data) => {
                this.setState({ data })
            })
    }

    componentDidMount() {
        this.fetchJsonUpdate()
    }

    componentDidUpdate() {
        this.fetchJsonUpdate()
    }

    render() {
        if (this.state.jsonResult !== null) {
            console.log(this.state.jsonResult)
            return (<div>Hello, {this.state.jsonResult}</div>)
        }
    }


}

export default GetJsonUpdate;
