import { Component } from 'react';

class GetJsonUpdate extends Component {
    constructor(props) {
        super(props)
        this.prevState = {
            x: null,
            y: null
        }
        this.state = {
            x: null,
            y: null
        }
    }

    fetchJsonUpdate = () => {
        fetch(`http://127.0.0.1:8080/api/json`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data)
                console.log("STATE: " + this.state)
                console.log("PREVSTATE: " + this.prevState)


                if ((this.state.x === null || this.state.y === null) || (this.prevState.x !== this.state.x && this.prevState.y !== this.state.y)) {
                    this.setState({ x: data.x, y: data.y })
                    this.prevState = { x: data.x, y: data.y }
                }
            })
    }

    componentDidMount() {
        this.fetchJsonUpdate()
        this.timer = setInterval(
            this.fetchJsonUpdate,
            500,
        );
    }

    componentWillUnmount() {
        clearInterval(this.timer)
    }

    render() {
        return (<div>Hello, {this.state.x} {this.state.y}</div>)
    }


}

export default GetJsonUpdate;
