import React, { useRef, useEffect } from 'react';

function App() {
	const canvas = useRef();
	let ctx = null;

	const drawFillRect = (info, style = {}) => {
		const { x, y, w, h } = info;
		const { backgroundColor = 'black' } = style;

		ctx.beginPath();
		ctx.fillStyle = backgroundColor;
		ctx.fillRect(x, y, w, h);
	}

	useEffect(() => {
		const canvasEle = canvas.current;
		canvasEle.width = 600;
		canvasEle.height = 600;

		// get context of the canvas
		ctx = canvasEle.getContext("2d");
	}, []);

	useEffect(() => {
		const r1Info = { x: 20, y: 30, w: 100, h: 50 };
		const r1Style = { borderColor: 'red', borderWidth: 10 };
		drawRect(r1Info, r1Style);

		const r2Info = { x: 100, y: 100, w: 80, h: 150 };
		drawRect(r2Info);

		const r3Info = { x: 250, y: 80, w: 80, h: 120 };
		drawFillRect(r3Info, { backgroundColor: 'green' });

		const r4Info = { x: 200, y: 220, w: 100, h: 50 };
		drawFillRect(r4Info);
	}, []);

	// draw rectangle
	const drawRect = (info, style = {}) => {
		const { x, y, w, h } = info;
		const { borderColor = 'black', borderWidth = 1 } = style;

		ctx.beginPath();
		ctx.strokeStyle = borderColor;
		ctx.lineWidth = borderWidth;
		ctx.rect(x, y, w, h);
		ctx.stroke();
	}

	return (
		<canvas ref={canvas}></canvas>
	);
}

export default App;
