import { Component } from 'react';

class GetJsonUpdate extends Component {
    constructor(props) {
        super(props)
        this.prevState = null
        this.state = {
            jsonRes: null
        }
    }

    fetchJsonUpdate = () => {
        fetch(`http://127.0.0.1:8080/api/json`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data)

                if (this.state.jsonRes === null || this.prevState !== this.state.jsonRes) {
                    this.setState({ jsonResult: data })
                    this.prevState = data
                }
            })
    }

    componentDidMount() {
        this.fetchJsonUpdate()
        this.timer = setInterval(
            this.fetchJsonUpdate,
            500,
        );
    }

    componentWillUnmount() {
        clearInterval(this.timer)
