import React, { useRef, useEffect } from 'react';

function App() {
	const canvas = useRef();
	let ctx = null;

	const drawFillRect = (info, style = {}) => {
		const { x, y, w, h } = info;
		const { backgroundColor = 'black' } = style;

		ctx.beginPath();
		ctx.fillStyle = backgroundColor;
		ctx.fillRect(x, y, w, h);
	}

	useEffect(() => {
		const canvasEle = canvas.current;
		canvasEle.width = 600;
		canvasEle.height = 600;

		ctx = canvasEle.getContext("2d");
	}, []);

	useEffect(() => {
		function customLoop(i) {
			console.log(i);
			i++;
			if (i <= 1000) {
				setTimeout(function () {
					const r4Info = { x: i, y: i, w: 100, h: 50 };
					drawFillRect(r4Info);
				}, 1000);
			}
		}
		customLoop(0);
	}, []);

	return (
		<canvas ref={canvas}></canvas>
	);
}

export default App;
